var _ = Schema

func ResourceInstanceRootVolumeSpec() *schema.Resource {
	res := &schema.Resource{
		Schema: map[string]*schema.Schema{
			"size":           OptionalInt(),
			"type":           OptionalString(),
			"iops":           OptionalInt(),
			"throughput":     OptionalInt(),
			"optimization":   OptionalBool(),
			"encryption":     OptionalBool(),
			"encryption_key": OptionalString(),
		},
	}

	return res
}

